import React from 'react'
import socketIOClient  from "socket.io-client";
import styles from './Spotter.module.css'

export default function Spotter (){

  

    const writeEvent = text => {
        // <ul> element
        const parent = document.querySelector('#events'); // <li> element
      
        const el = document.createElement('li');
        el.innerHTML = text;
        //parent.appendChild(el);
      };
      
      const onFormSubmitted = e => {
        e.preventDefault();
        const input = document.querySelector('#chat');
        const text = input.value;
        input.value = '';
        sock.emit('message', text);
      };
      
      const addButtonListeners = () => {
        ['rock', 'paper', 'scissors'].forEach(id => {
          const button = document.getElementById(id);
         // button.addEventListener('click', () => {//
         ////   sock.emit('turn', id);
         // });
        });
      };
      
      writeEvent('Welcome to RPS');
      const sock = socketIOClient();
      sock.on('message', writeEvent);
    //  document.querySelector('#chat-form').addEventListener('submit', onFormSubmitted);
      addButtonListeners();

    
        return(
            <div className={styles.rpswrapper}>
            <ul id="events"></ul>
            <div class="controls">
              <div class="chat-wrapper">
                <form id="chat-form">
                  <input id="chat" autocomplete="off" title="chat"/>
                  <button id="say">Say</button>
                </form>
              </div>
      
              <div class="button-wrapper">
                <button id="rock" class="turn">1</button>
                <button id="paper" class="turn">2</button>
                <button id="scissors" class="turn">3</button>
              </div>
            </div>
          </div>
        )
    }
