import axios from 'axios';
import React, { Component } from 'react'
import styles from './Login.module.css'
import socketIOClient  from "socket.io-client";
//import App from '../../App'
//import Connection from '../../service/service'

//let con = new Connection();
const URL = 'http://localhost:7621/player/login'

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username : '',
            password : '',
            check:true,
            check2:true,
            stat:0,
            connected:"Connected",
            waiting:"Waiting...",
            client : 'http://127.0.0.1:7621',
            number:3,
            color:'Game is starting'
        }

    }

    onChange = (e) =>
    this.setState({ [e.target.name]: e.target.value });

    LoginCustomer = (e) =>
    {     
        e.preventDefault()
        let customer = { username: this.state.username, password: this.state.password };
        axios.post(URL,customer).then((res)=>res.data).then((status) => {
        if(status === "One User Logged" || status === "Both Users Logged"){
            console.log("User Authenticated");
            console.log(status); 
            this.setState({check:!this.state.check})

            if(status === "Both Users Logged"){
                this.send()
                const socket = socketIOClient(this.state.client);
                socket.emit('startgame', this.state.number)
                socket.on('starts', (test) => {
                console.log("yes",test)
               }) 
            }

        } else {
           alert("Access Denied");
        }
     })
     
    }

    componentDidMount(){
        //this.interval = setInterval(()=>
        //this.check(),1000);
    }
    componentWillUnmount(){
        clearInterval(this.interval);
    }
   
send = () => {
    const socket = socketIOClient(this.state.client);
    socket.emit('change color', this.state.color) // change 'red' to this.state.color
  }


    render() {
        const socket = socketIOClient(this.state.client);
         socket.on('change color', (col) => {
        
          console.log(col)
          this.props.history.push('GameStart')
    })
        return (
         
            <React.Fragment>
               
               <div className="container">
        <div className="circleTop" />
        <div className="content">
          <h1>Login</h1>
          <div className="form-group">
            <label htmlFor>Email</label>
            <br />
            <input type="email" className="form" name id aria-describedby="helpId" placeholder />
          </div>
          <div className="form-group">
            <label htmlFor>Password</label>
            <br />
            <input type="password" className="form" name id placeholder />
          </div>
          <p>
            <a href="index.html"> Forgot Password?</a>
          </p>
          <div className="icons">
            <a href="http://www.google-plus.com"><i className="fa fa-google-plus-official" aria-hidden="true" />
            </a>
            <a href="https://www.facebook.com"><i className="fa fa-facebook-square" aria-hidden="true" />
            </a>
            <a href="http://www.twitter.com"><i className="fa fa-twitter-square" aria-hidden="true" />
            </a>
          </div>
          <button type="button"><a href="index.html">Login</a></button>
        </div>
        <div className="circleBottom" />
      </div>

        </React.Fragment>
       )
    }
}

export default Login
