import React from 'react'
import styles from './Game.module.css'

export default class Game extends React.Component{
    constructor(props){
        super(props)
        this.state = {
           position:'Dealer',
           number:""
        }
    }
    onChange = (e) =>
    this.setState({ [e.target.name]: e.target.value });


    Test = () => {
        console.log("Number: ",this.state.number)
        const socket = socketIOClient(this.state.client);
         socket.emit('startgame', this.state.number)
         socket.on('starts', (test) => {
         console.log("yes",test)
         })
    }


    render(){
        return(
            <div>         
                   <h3> Your now playing as {this.state.position}</h3>
            <div className={styles.gamecontainer}>
             <h3 className={styles.gametext}>Enter a number between 1 - 3</h3>
                <input className={styles.gameinput} type="text" name="number" value={this.state.number} onChange={this.onChange}></input>
                <br></br>
                <br></br>
                <center><button onClick={this.Test}>Submit</button></center>
            </div>
            </div>
        )
    }
}
