import axios from 'axios';
import React, { Component } from 'react'
import styles from './Login.module.css'
import socketIOClient  from "socket.io-client";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBCard, MDBInput } from 'mdbreact';
//import App from '../../App'
//import Connection from '../../service/service'

//let con = new Connection();
const URL = 'http://localhost:7621/player/login'

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username : '',
            password : '',
            check:true,
            check2:true,
            stat:0,
            connected:"Connected",
            waiting:"Waiting...",
            client : 'http://127.0.0.1:7621',
            number:3,
            color:'Game is starting'
        }

    }

    onChange = (e) =>
    this.setState({ [e.target.name]: e.target.value });

    LoginCustomer = (e) =>
    {     
        e.preventDefault()
        let customer = { username: this.state.username, password: this.state.password };
        axios.post(URL,customer).then((res)=>res.data).then((status) => {
        if(status === "One User Logged" || status === "Both Users Logged"){
            console.log("User Authenticated");
            console.log(status); 
            this.setState({check:!this.state.check})

            if(status === "Both Users Logged"){
                this.send()
                const socket = socketIOClient(this.state.client);
                socket.emit('startgame', this.state.number)
                socket.on('starts', (test) => {
                console.log("yes",test)
               }) 
            }

        } else {
           alert("Access Denied");
        }
     })
     
    }

    componentDidMount(){
        //this.interval = setInterval(()=>
        //this.check(),1000);
    }
    componentWillUnmount(){
        clearInterval(this.interval);
    }
   
send = () => {
    const socket = socketIOClient(this.state.client);
    socket.emit('change color', this.state.color) // change 'red' to this.state.color
  }


    render() {
        const socket = socketIOClient(this.state.client);
         socket.on('change color', (col) => {
        
          console.log(col)
          this.props.history.push('GameStart')
    })
        return (
         
            <React.Fragment>
   <MDBContainer>
      <MDBRow>
        <MDBCol md='6'>
          <MDBCard
            className='card-image'
            style={{
              backgroundImage:
                'url(https://mdbootstrap.com/img/Photos/Others/pricing-table7.jpg)',
              width: '28rem'
            }}
          >
            <div className='text-white rgba-stylish-strong py-5 px-5 z-depth-4'>
              <div className='text-center'>
                <h3 className='white-text mb-5 mt-4 font-weight-bold'>
                  <strong>SIGN</strong>
                  <a href='#!' className='green-text font-weight-bold'>
                    <strong> UP</strong>
                  </a>
                </h3>
              </div>
              <MDBInput
                label='Your email'
                group
                type='text'
                validate
                labelClass='white-text'
              />
              <MDBInput
                label='Your password'
                group
                type='password'
                validate
                labelClass='white-text'
              />
              <div className='md-form pb-3'>
                <MDBInput
                  label={
                    <>
                      Accept the&nbsp;
                      <a href='#!' className='green-text font-weight-bold'>
                        Terms and Conditions
                      </a>
                    </>
                  }
                  type='checkbox'
                  id='checkbox1'
                  labelClass='white-text'
                />
              </div>
              <MDBRow className='d-flex align-items-center mb-4'>
                <div className='text-center mb-3 col-md-12'>
                  <MDBBtn
                    color='success'
                    rounded
                    type='button'
                    className='btn-block z-depth-1'
                  >
                    Sign in
                  </MDBBtn>
                </div>
              </MDBRow>
              <MDBCol md='12'>
                <p className='font-small white-text d-flex justify-content-end'>
                  Have an account?
                  <a href='#!' className='green-text ml-1 font-weight-bold'>
                    Log in
                  </a>
                </p>
              </MDBCol>
            </div>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>


        </React.Fragment>
       )
    }
}

export default Login
